import React, { useState, useEffect } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";


const styles = {
  cardCategoryWhite: {
    "&,& a,& a:hover,& a:focus": {
      color: "rgba(255,255,255,.62)",
      margin: "0",
      fontSize: "14px",
      marginTop: "0",
      marginBottom: "0"
    },
    "& a,& a:hover,& a:focus": {
      color: "#FFFFFF"
    }
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
    "& small": {
      color: "#777",
      fontSize: "65%",
      fontWeight: "400",
      lineHeight: "1"
    }
  }
};

const useStyles = makeStyles(styles);

export default function ListaDeUsuarios() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [data, setData] = useState();
  const classes = useStyles();

  function addResult(r) {
    console.log(r)
    teste = r;
  }

  async function getDatas() {
    return fetch
      (
        "http://192.168.15.10:3200/api/admin/getAllUsers",
        {
          method: 'GET',
          mode: 'no-cors',
        }
      )
      .then(res => res.json())
      .then(
        (result) => {
          return result.json();
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }

  async function CarregarDados() {
    let teste = await getDatas()
    setData(teste);
    setIsLoading(false);
  }

  if(isLoaded){
    CarregarDados()
  }

  function Content() {
    if (!isLoaded) {
      return (
        <h3>Carregando</h3>
      );
    } else {
      return (
        <GridContainer>
          <GridItem xs={12} sm={12} md={12}>
            <Card plain>
              <CardHeader plain color="primary">
                <h4 className={classes.cardTitleWhite}>
                  Lista de Usuários
            </h4>
                <p className={classes.cardCategoryWhite}>
                  Filtre por tipo de usuário e localização.
            </p>
              </CardHeader>
              <CardBody>
                <Table
                  tableHeaderColor="primary"
                  tableHead={["ID", "Nome", "Tipo De Perfil", "Cidade", "Estado", "Quantidade de Links"]}
                  // tableData={[
                  //   ["149ye9ee9ee", "Teste", "2", "teste", "teste", "2"],

                  // ]}
                  tableData={data}
                />
              </CardBody>
            </Card>
          </GridItem>
        </GridContainer>
      );
    }
  }

  return (
    <Content />
  );
}
